import React, { Component } from 'react'
import './App.css'


export default class App extends Component {


  state= {
    numeroDeCliques2: 0,
    numeroDeCliques3: 0
  }

  // com arrow function 
  msgPrint2 = () => {
   const estadoAtual = this.setState((bla,_props)=> ({
     numeroDeCliques2: bla.numeroDeCliques2 + 1 
    }))
    console.log(this.setState.name);
  }
  msgPrint3 = () => {
    this.setState((x,_props) => ({
      numeroDeCliques3: x.numeroDeCliques3 + 1
    }))
  }
  render() {
    const {numeroDeCliques2,numeroDeCliques3,num} = this.state;
    return (
      <div>

        <button onClick={this.msgPrint2}>{numeroDeCliques2}</button>
        <button onClick={this.msgPrint3}>{numeroDeCliques3}</button>
      </div>
    )
  }
}
