import React, { Component } from 'react'
import './App.css'


export default class App extends Component {

  constructor () {

    super ()

    this.msgPrint = this.msgPrint.bind(this);
    this.state = {
      numeroDeCliques: 0
    }
  }
  msgPrint () {
    this.setState((stado,_props) => ({
      numeroDeCliques: stado.numeroDeCliques +1 
    }))
    console.log('oioi')
  }
  // com arrow function 
  msgPrint2 = () => {
    console.log('legal')
  }
  msgPrint3 () {
    console.log('bom dia')
    console.log(this)
  }
  render() {
    const {numeroDeCliques} = this.state;
    return (
      <div>
        <button onClick={this.msgPrint}>{numeroDeCliques}</button>
        <button onClick={this.msgPrint2}>Mybutton</button>
        <button onClick={this.msgPrint3}>Mybutton</button>
      </div>
    )
  }
}
