import React, { Component } from 'react'
import './App.css'


export default class App extends Component {

  constructor () {

    super ()

    this.msgPrint = this.msgPrint.bind(this);
    this.state = {
      numeroDeCliques: 0
    }
  }

  // msgPrint () {
  //   this.setState((stado,_props) => ({
  //     numeroDeCliques: stado.numeroDeCliques +1 
  //   }))
  //   console.log('oioi')
  // }
  // state = {
  //   numeroDeCliques2: 0
  // }


  // com arrow function 
  msgPrint2 = () => {
    this.setState((bla,_props)=> ({
     numeroDeCliques2: bla.numeroDeCliques2 + 1 
    }))
    console.log('legal')
  }
  msgPrint3 = () => {
    console.log('foi')
  }
  render() {
    const {numeroDeCliques} = this.state;
    const {numeroDeCliques2} = this.state;
    return (
      <div>
        <button onClick={this.msgPrint}>{numeroDeCliques}</button>
        <button onClick={this.msgPrint2}>{numeroDeCliques2}</button>
        <button onClick={this.msgPrint3}>Mybutton</button>
      </div>
    )
  }
}
