import React, { Component } from 'react'

export default class Form extends Component {
  state = {
    texto : '',
    coisa : '',
    name : '',
    idade: 0,
  }

  handleTChange = ({target}) => {
    const {name, value} = target;
    this.setState({[name] : value})
  }
    render() {
    return (
      <div className='forms'>Form
        <label>
            Diga alguma coisa
            <textarea name="texto" value={this.state.texto}
            onChange={this.handleChange} cols="30" rows="10"></textarea>
        </label>
        <label >
            Selicione a coisa
            <select value={this.state.coisa} onChange={this.handleChange}  name="coisas" >
                <option value="valor1" >uma coisa</option>
                <option value="valor 2" >outra coisa</option>
            </select>
        </label>
        <label>
            nome
            <input value={this.state.name} name='nome' type="text" onChange={this.handleChange} />
        </label>
        <label >
            idade
            <input value={this.state.idade} name='idade' type="number" onChange={this.handleChange}/>
        </label>

      </div>
    )
  }
}
