import React, { Component } from 'react'

export default class Form extends Component {
  state = {
    texto : '',
  }

  handleTChange = ({target}) => {
    const {name, value} = target;
    this.setState({[name] : value})
  }
    render() {
    return (
      <div className='forms'>Form
        <label>
            Diga alguma coisa
            <textarea name="texto" 
            onChange={this.handleChange} cols="30" rows="10"></textarea>
        </label>
        <label >
            Selicione a coisa
            <select name="coisas" >
                <option value="valor1" onChange={this.handleChange}>uma coisa</option>
                <option value="valor 2" onChange={this.handleChange}>outra coisa</option>
            </select>
        </label>
        <label>
            nome
            <input name='nome' type="text" onChange={this.handleChange} />
        </label>
        <label >
            idade
            <input name='idade' type="number" onChange={this.handleChange}/>
        </label>

      </div>
    )
  }
}
