import React, { Component } from 'react'

export default class Form extends Component {
  state = {
    texto : '',
  }

  handleTextAreaChange = ({target}) => {
    console.log(target.name, target.value)
    this.setState({texto : target.value})
  }
    render() {
    return (
      <div className='forms'>Form
        <label>
            Diga alguma coisa
            <textarea name="texto" value={this.state.texto}
            onChange={this.handleTextAreaChange} cols="30" rows="10"></textarea>
        </label>
        <label >
            Selicione a coisa
            <select name="coisas" >
                <option value="valor1">uma coisa</option>
                <option value="valor 2">outra coisa</option>
            </select>
        </label>
        <label>
            nome
            <input name='nome' type="text" />
        </label>
        <label >
            idade
            <input name='idade' type="number" />
        </label>

      </div>
    )
  }
}
