import React, { Component } from 'react'
import User from './components/User';

export default class App extends Component {
  state = {
    userAPI : [],
  }
  async fetchUser() {
    const request = await fetch('https://api.randomuser.me/');
    const renponse = await request.json()
    this.setState({
      userAPI : [...renponse.results]
    })
  }
  componentDidMount() {
    this.fetchUser()
  }
  render() {
    const {dob:{email,age},name:{title,first,last},picture:{thumbnail}} = this.state[0]

    return (
      <div>
        <User 
        nome={`${title} ${first} ${last}`}
        email={email}
        idade={age}
        image={thumbnail}/>
      </div>
    )
  }
}
