import React, { Component } from 'react'
import User from './components/User';

export default class App extends Component {
  state = {
    loading: true,
    userAPI : undefined,
  }
  async fetchUser() {

    this.setState({
      loading : true,
    }, async () => {
      const request = await fetch('https://api.randomuser.me/');
      const response = await request.json()
      this.setState({
        userAPI: response.results,
        loading: false,
      })
    })

  }
  componentDidMount() {
    this.fetchUser()

  }
  render() {
    const LOAD = <p>Carregando</p>;

    const {userAPI, loading} = this.state
    const {dob:{email,age},name:{title,first,last},picture:{thumbnail}} = userAPI

    return (
      <div>
        {  loading ? LOAD : <User 
        nome={`${title} ${first} ${last}`}
        email={email}
        idade={age}
        image={thumbnail}/>  }
      </div>
    )
  }
}
