import React, { Component } from 'react'

export default class User extends Component {
  render() {
    const {nome, email, idade, image} = this.props
    return (
        // renderizado exibindo foto, nome, email e idade do usuário.

      <div>
        <p>{nome}</p>
        <p>{email}</p>
        <p>{idade}</p>
        <img src={image} alt={nome} />
      </div>
    )
  }
}
