import React, { Component } from 'react'

export default class Form extends Component {
  render() {
    const { handleSubmit, handleChange, handleClick, email, isDisabled } = this.props;
    return (
      <div>
        <form 

        onSubmit={ handleSubmit }
        >
         <label htmlFor="email">
            Email:
            <input
            data-testid="inputEmail"
            id="email"
            onChange={ handleChange }
            name="email"
            value={ email } 
            type="email" />
            </label>  
            <button
            data-testid='button'
            type="submit"
            value="Enviar"
            disabled={ isDisabled }>    
            Enviar    
            </button>
            <button
            onClick={ handleClick }

            type="button">
            Voltar    
            </button> 
        </form>
      </div>
    )
  }
}
