import React, { Component } from "react";
import Form from "./components/Form";

export default class App extends Component {
  state = {
    email: "",
    saveEmail: "",
  };

  handleChange = ({ target: { name, value } }) => {
    this.setState({
      email: value,
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    this.setState({
      saveEmail: this.state.email,
      email: "",
    });
  };
  // handleClick = () => {
  //   this.setState({
  //     submit: false,
  //   })

  // }
  render() {
    const { saveEmail, email } = this.state;
    return (
      <div>
        <header>
          <h1>RLT - introdução </h1>
        </header>
        <Form
          handleSubmit={this.handleSubmit}
          handleChange={this.handleChange}
          handleClick={this.handleClick}
          // isDisabled={email === ""}
          email={email}
        />

        <div>
          <span data-testid="emailText">{`Email: ${saveEmail}`}</span>
        </div>
      </div>
    );
  }
}
