import React, { Component } from "react"
import ValidEmail from "./components/ValidEmail";

export default class App extends Component {
  state = {
    email: "",
    saveEmail: "",
  };

  handleChange = ({ target: { name, value } }) => {
    this.setState({
      email: value,
    });
  };

  handleClick = () => {
    this.setState({
      saveEmail: this.state.email,
      email: "",
    });
  };
  // handleClick = () => {
  //   this.setState({
  //     submit: false,
  //   })

  // }
  render() {
    const { saveEmail, email } = this.state;
    return (
      <div>
        <header>
          <h1>RLT - introdução </h1>
        </header>
        <form 
        >
         <label htmlFor="email">
            Email:
            <input
            id="email"
            onChange={ this.handleChange }
            name="email"
            value={ email } 
            type="email" />
            </label>  
            <button
            onClick={ this.handleClick }
            data-testid='button'
            type="button"
            value="Enviar">    
            Enviar    
            </button>
            <button
            type="button">
            Voltar    
            </button> 
        </form>
      <ValidEmail email={ saveEmail}  />
      </div>
    );
  }
}
