import React, { Component } from 'react'
import Form from './components/Form'

export default class App extends Component {
  state = {
    email: "",
    submit: false,
    infos: {},
  }

  handleChange = ({target:{name, value}}) => {
      this.setState({
        [name] : value,
      })
  }

  handleSubmit = (e) => {
    e.preventDefault();
    this.setState({
      submit: true,
      infos: { email: this.state.email },
      email: ""
    })
  }
  render() {
    const { email, infos, submit } = this.state;
    return (
      <div>
        <header >
     <h1>RLT - introdução </h1>
      </header>
      <Form
      handleSubmit={ this.handleSubmit }
      handleChange= { this.handleChange }
      // handleClick= { handleClick } 
      email= { email } />

     { submit && ( <div>
        <span>Email:</span><span>{ infos.email }</span>
      </div> )}

      </div>
    )
  }
}
