import React, { Component } from 'react'
import { connect } from 'react-redux'
import addAssignment from '../actions/index';

export class InputList extends Component {
    state = {
        textValue: ''
    }
  render() {
    const { add } = this.props;
    console.log(this.props)
    const { textValue } = this.state;
    return (
      <div>
        <input type="text"
        placeholder='Digite a tarefa'
        onChange={ (e) => this.setState({ textValue: e.target.value })} />
        <button
        type='button'
        onClick={ () => add(textValue)}>
            Adicionar Tarefa
        </button>
      </div>
    )
  }
}

const mapDispatchToProps = (dispatch) => ({
    add: (value) => dispatch(addAssignment(value))
})

export default connect(null, mapDispatchToProps)(InputList) //retorna uma função que é chamada em seguida
