import React, { Component } from 'react'

export default class App extends Component {
  state = {
    email : '',
  }
  handleChange = ({target:{name, value}}) => {
    this.setState({[name]: value})
  }
  render() {
    return (
      <div>
        <h1>Teste</h1>
        <label htmlFor='email'>
          <input
          onChange={this.handleChange}
          type="text"
          name='email'
          id='email'>

          </input>
          <button
          type='button'>Salvar</button>
        </label>
      </div>
    )
  }
}
