import { combineReducers } from "redux";

const INITIAL_STATE = {
    isFetching: false,
    imagePath: '',
    error: '',
};

const reducer = (state = INITIAL_STATE, action) => {
    switch(action.type) {
        default:
            return state;
    }
}

const rootReducer = combineReducers({ reducer })

export default rootReducer;
